export const HELLO_POST = 'HELLO_POST';

export const helloPost = {
    type: HELLO_POST,
    data: dumdumdummy
}

export const ADD_DUMMY = 'ADD_DUMMY';

export const ADD_POST = 'ADD_POST';

export const addDummy = {
    type: ADD_DUMMY,
    data: {
        postTitle : 'Hello',
        postContent : 'fucking rain',
        User: {
            nickname: 'test user',
            profileImage: 'https://scontent-icn1-1.cdninstagram.com/vp/559b8a8ece4f07cf6e7a457f600968b4/5E4BC5CB/t51.2885-15/e35/s1080x1080/72695011_515793855687177_6497185184176620114_n.jpg?_nc_ht=scontent-icn1-1.cdninstagram.com&_nc_cat=1'
        },
        image: '',
        createdAt: new Date(),
    }
}

const addPost = {
    type: ADD_POST,
}

const reducer = (state = initialState, action ) => {
    switch(action.type){
        case HELLO_POST: {
            return{
                ...state,
                mainPosts: dumdumdummy
            }
        }
        case ADD_POST: {
            return {
                ...state,
            }
        }

        case ADD_DUMMY: {
            return {
                ...state,
                mainPosts: [action.data, ...state.mainPosts]
            }
        }

        default: {
            return {
                ...state
            }
        }
    }
}

export default reducer;
