import React from 'react';
import PropTypes from 'prop-types';

const dummy = {
    isLoggiedIn: true,
    imagePaths: [],
    mainPosts: [{
        User: {
            id: 1,
            nickname: 'anjoy',
        },
        content: '첫 번째 게시글',
        img: 'https://cafeptthumb-phinf.pstatic.net/MjAxODEwMTdfMTEy/MDAxNTM5Nzc5MDQyNDgz.1c9fV0imO8toanzl4DG-9fb4-MLMbodpjdpApsBIbEAg.hgaKF2X2cmiO-hYT7T98uqXoNUenEshBmCdkoSLc7Zog.JPEG.ajjj12345/IMG_20181017_204128.jpg?type=w740',
    }],
}

const TestCard = ( ) => {
    return (
        <div style={{width:'250px', height:'500px', display:'flex', flexDirection: 'column', position:'relative', borderRadius:'10px', boxShadow:'0 0 4px #999'}}>
            <div style={{position:'absolute', width:'100%', height: '100%', top:0, bottom:0, backgroundColor:'red'}} > </div>
            <div style={{position:'relative', padding:'2rem 1rem', display:'flex', flexDirection:'column', flex:'1 1'}}>
                <a style={{position:'absolute', width:'3.5rem', height:'3.5rem', top:0, right:'1rem', backgroundColor:'white', alignContent: 'center', justifyContent: 'center', borderRadius: '50%'}}>
                    <img 
                        src="https://thumb.velog.io/resize?url=https://images.velog.io/images/dus532/profile/666b6b60-f577-11e9-b0d9-e36969e4a710/uiux.png"
                        style={{
                            display:'block',
                            width: '2.5rem',
                            height: '2.5rem',
                            objectFit:'cover',
                            borderRadius:'50%',
                        }}/>
                </a>
            </div>
        </div>
    );
};

TestCard.propTypes = {

};

export default TestCard;
